def solution(S):

    operations=0
    #calculating index of first one in string
    first_one = S.find("1")
    #if
    if first_one==-1:
        operations=0
    else:
        #calculating operations
        operations = len(S.replace("0","")) + len(S) - first_one - 1

    #returning operations
    return operations


if __name__ == "__main__":
    print(solution('011100'))
    print(solution('111'))
    print(solution('1'))
    print(solution('1111010101111'))
    print(solution(''))
